export const productReducer = (state ={products:[]},action) =>{
    switch(action.type){
        case 'PRODUCT_LIST_REQUEST':
            return {
                loading:true,
                products:[]
            }
        case 'PRODUCT_LIST_SUCCESS':
            return {
                loading:false,products:acion.payload
            }
        case 'PRODUCT_LIST_FAIL':
            return {
                loading:false,
                error:action.payload
            }
        default:
            return state
    }
}
